$(function () {
    var header = $("nav");
    var objeto = $(".imagencorp");

    $(window).scroll(function (event) {

        height = $(event.target).scrollTop();

        if (height > 500) {
            header.addClass("navbar navbar-fixed-top custom-navbar top-nav-collapse");
            objeto.removeClass("logo-l").addClass("logo");
        } else {
            header.removeClass("navbar navbar-fixed-top custom-navbar top-nav-collapse").addClass("navbar custom-navbar");
            objeto.removeClass("logo").addClass("logo-l");
        }
    });
});


