$(document).ready(function(){
	$("#formulario").submit(function( event ){
		event.preventDefault();
		$.ajax({
			type: 'POST',
			url: 'proceso.php',
			data: $(this).serialize(),
			success: function(data){
				$("#respuesta").html(data);
			}
		});

		return false;
	});
});

